  `capitol2` varchar(1000) DEFAULT NULL,
  `capitol3` varchar(1000) DEFAULT NULL,
  `titluCapitol1` varchar(100) DEFAULT NULL,
  `titluCapitol2` varchar(100) DEFAULT NULL,
  `titluCapitol3` varchar(100) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

